package com.it.service;

import java.util.List;

import com.it.bean.GoodsInfo;
import com.it.dao.GoodsDao;

public class GoodsImpl implements IGoods{
	
	GoodsDao goodsDao = new GoodsDao();

	@Override
	public List<GoodsInfo> queryAllGoods(GoodsInfo goods) {
		// TODO Auto-generated method stub
		return goodsDao.queryAllGoods(goods);
	}

	@Override
	public GoodsInfo findGoodsById(GoodsInfo goods) {
		// TODO Auto-generated method stub
		return goodsDao.findGoodsById(goods);
	}

}
